mapper.update:
UPDATE user
mobile = #{mobile, jdbcType=VARCHAR}, nick_name = #{nickName, jdbcType=VARCHAR}, avatar = #{avatar, jdbcType=VARCHAR}, gender = #{gender, jdcbType=INTEGER}, living_city = #{livingCity, jdbcType=VARCHAR}, sport_target = #{sportTarget, jdbcType=VARCHAR}, user_id=#{userId, jdbcType=INTEGER}
WHERE user_id=#{userId, jdbcType=INTEGER}

UPDATE user
set password=#{password}
where user_id=#{userId, jdbcType=INTEGER}

mapper.select:
SELECT user_id, nick_name, avatar, gender
FROM user
WHERE mobile=#{mobile, jdbcType=VARCHAR}

SELECT user_id, nick_name, avatar, gender, living_city
FROM user
WHERE living_city=#{city}

SELECT user_id, nick_name, avatar, gender, sport_target
FROM user
WHERE sport_target=#{target}

SELECT password
FROM user
WHERE nick_name=#{name}
